import React, { useRef, useEffect, useState } from 'react';
import ReactPlayer from 'react-player/lazy';

const setPlayingState = (newPlayingVideos, playingVideos, videoRefs) => {
  videoRefs.current.forEach((ref) => {
    const videoId = ref.current?.getAttribute('data-id');
    if (newPlayingVideos.includes(videoId)) {
      if (!playingVideos.includes(videoId)) {
        ref.current.seekTo(0);
        ref.current.play();
      }
    } else {
      if (playingVideos.includes(videoId)) {
        ref.current.pause();
      }
    }
  });
  return newPlayingVideos;
};

const WatchPage = () => {

  const videos = [
    { 
      id: 1,
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    },
    { 
      id: 2,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    // { 
    //   id: 3,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 4,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 5,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 6,
    //   url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    // },
    // { 
    //   id: 7,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 8,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 9,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 10,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 11,
    //   url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    // },
    // { 
    //   id: 12,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 13,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 14,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
    // { 
    //   id: 15,
    //   url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    // },
   
  ];

  // hàm xáo trộn danh sách video
  const shuffleVideos = (videos) => {
    for (let i = videos.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [videos[i], videos[j]] = [videos[j], videos[i]];
    }
    return videos;
  };

  const shuffledVideos = shuffleVideos(videos); // xáo trộn danh sách video
  
  const videoRefs = useRef([]);
  const [playingVideos, setPlayingVideos] = useState([]);

  const handleError = (error) => {
    console.log('An error occurred:', error);
  };

  const handleVideoChange = (video) => {
    const newPlayingVideos = [video.id.toString()];
    setPlayingVideos((prevPlayingVideos) => {
      if (prevPlayingVideos.includes(newPlayingVideos[0])) {
        return prevPlayingVideos;
      }
      setPlayingState(newPlayingVideos, prevPlayingVideos, videoRefs);
      return newPlayingVideos;
    });
  };
  useEffect(() => {
    setPlayingState(playingVideos, playingVideos, videoRefs);
  }, [playingVideos]);
  useEffect(() => {
    // hàm kiểm tra xem video có đang nằm trong phạm vi màn hình hay không
    const handleIntersection = (entries) => {
      entries.forEach((entry) => {
        const videoId = entry.target?.getAttribute('data-id');
        const isPlaying = playingVideos.includes(videoId);
        const videoIndex = videoRefs.current.findIndex((ref) => ref.current.getAttribute('data-id') === videoId);
    
        if (entry.isIntersecting && !isPlaying) {
          const videoRef = videoRefs.current[videoIndex];
          if (videoRef) {
            videoRef.current.seekTo(0);
            videoRef.current.play();
          }
          setPlayingVideos((prevPlayingVideos) => setPlayingState([...prevPlayingVideos, videoId], prevPlayingVideos, videoRefs));
        } else if (!entry.isIntersecting && isPlaying) {
          const videoRef = videoRefs.current[videoIndex];
          if (videoRef) {
            videoRef.current.pause();
          }
          setPlayingVideos((prevPlayingVideos) => setPlayingState(prevPlayingVideos.filter((id) => id !== videoId), prevPlayingVideos, videoRefs));
        }
      });
    };
    

    const observer = new IntersectionObserver(handleIntersection, {
      threshold: 0.5,
    });
    
    const refs = videoRefs.current;
    refs.forEach((ref) => {
      if (ref.current) {
        observer.observe(ref.current);
      }
    });
  
    return () => {
      refs.forEach((ref) => {
        if (ref.current) {
          observer.unobserve(ref.current);
        }
      });
    };
  }, [playingVideos, videoRefs]);

  return (
    <div className="max-w-screen max-h-screen overflow-auto">
      <div className="flex flex-wrap justify-center items-center mt-[60px]">
        {shuffledVideos.map((video,i) => (
          <div key={video.id} className="flex justify-center items-center mb-[45px] shadow-2xl rounded-2xl overflow-hidden">
            <ReactPlayer
              key={video.id} // sử dụng giá trị video.id làm key
              ref={(ref) => videoRefs.current[i] = ref}
              data-id={video.id}
              url={video.url}
              playing={playingVideos.includes(video.id)}
              muted={true}
              autoPlay={true}
              onError={handleError}
              width="960px"
              height="540px"
              controls
              allowFullScreen={true}
              onClick={handleVideoChange}
              config={{
                youtube: {
                  playerVars: { showinfo: 1 }
                }
              }}
              loading="lazy" // sử dụng lazy load
            /> 
            {console.log(playingVideos)}
          </div>
        ))}
      </div>
    </div>
  );
};

export default WatchPage;
