import ReactPlayer from "react-player";
import React, { useState } from "react";

const VideoPlayer = () => {
  const [playing, setPlaying] = useState(false);
  const [muted, setMuted] = useState(true);

  const handleReady = () => {
    setPlaying(true);
    setMuted(false);
  };

  const handlePlay = () => {
    setPlaying(true);
    setMuted(false);
  };

  const videos = [
    {
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
      aspectRatio: "aspect-w-16 aspect-h-9",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      aspectRatio: "aspect-w-16 aspect-h-9",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      aspectRatio: "aspect-w-16 aspect-h-9",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      aspectRatio: "aspect-w-16 aspect-h-9",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      aspectRatio: "aspect-w-16 aspect-h-9",
    },
  ];

  return (
    <div className="max-w-screen max-h-screen overflow-auto">
      <div className="flex flex-wrap justify-center -mx-4">
        {videos.map((video, index) => (
          <div
            key={index}
            className="w-full sm:w-full md:w-1/2 lg:w-1/3 xl:w-1/4 px-4 sm:mt-4 md:mt-8 lg:mt-12 xl:mt-16"
          >
            <div className={`relative ${video.aspectRatio}`}>
              <ReactPlayer
                url={video.url}
                playing={playing}
                muted={muted}
                autoPlay={true}
                onReady={handleReady}
                onPlay={handlePlay}
                controls
                allowfullscreen
                width="100%"
                height="100%"
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideoPlayer;
