import React, { useState } from 'react';
import ReactPlayer from 'react-player/lazy';

const WatchPage = () => {
  const [playingVideo, setPlayingVideo] = useState(null); // video đang phát
  const videoRefs = useRef([]);

  const handleError = (error) => {
    console.log('An error occurred:', error);
  };

  const videos = [
    { 
      id: 1,
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    },
    { 
      id: 2,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 3,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 4,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 5,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 6,
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    },
    { 
      id: 7,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 8,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 9,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 10,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    }
   
  ];

  // hàm xáo trộn danh sách video
  const shuffleVideos = (videos) => {
    for (let i = videos.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [videos[i], videos[j]] = [videos[j], videos[i]];
    }
    return videos;
  };
  const handleVideoClick = (videoId) => {
    setPlayingVideo(videoId);
  };
  const shuffledVideos = shuffleVideos(videos); // xáo trộn danh sách video

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const videoId = parseInt(entry.target.getAttribute('data-video-id'));
          setPlayingVideo(videoId);
        } else {
          const videoId = parseInt(entry.target.getAttribute('data-video-id'));
          if (videoId === playingVideo) {
            setPlayingVideo(null);
          }
        }
      });
    }, { threshold: 0.5 });

    videoRefs.current.forEach((videoRef) => {
      observer.observe(videoRef);
    });

    return () => {
      observer.disconnect();
    };
  }, [playingVideo]);
  return (
    <div className="max-w-screen max-h-screen overflow-auto">
      <div className="flex flex-wrap justify-center items-center mt-[60px]">
        {shuffledVideos.map((video, i) => (
          <div key={video.id} className="flex justify-center items-center mb-[45px] shadow-2xl rounded-2xl overflow-hidden">
            <ReactPlayer
              url={video.url}
              playing={true}
              muted={false}
              autoPlay={true}
              onError={handleError}
              width="960px"
              height="540px"
              controls
              allowFullScreen={true}
              config={{
                youtube: {
                  playerVars: {
                    origin: window.location.origin
                  }
                }
              }}
            /> 
          </div>
        ))}
      </div>
    </div>
  );
};

export default WatchPage;
