import ReactPlayer from 'react-player';
import React, { useState } from 'react';

const VideoPlayer = () => {
  const [playing, setPlaying] = useState(false);
  const [muted, setMuted] = useState(true);

  const handleReady = () => {
    setPlaying(true);
    setMuted(false);
  };

  const handlePlay = () => {
    setPlaying(true);
    setMuted(false);
  };

  return (
    <div className="flex justify-center items-center w-screen h-screen">
      <div className="aspect-w-16 aspect-h-9 mx-auto rounded-80">
        <div cl>
          <ReactPlayer
            url="https://www.youtube.com/watch?v=KxaX-eYPjQw"
            playing={playing}
            muted={muted}
            autoPlay={true}
            onReady={handleReady}
            onPlay={handlePlay}
            controls
            allowfullscreen
            width="940px" 
            height="530px"
          />
        </div>
        
        <ReactPlayer
          url="https://www.youtube.com/watch?v=KxaX-eYPjQw"
          playing={playing}
          muted={muted}
          autoPlay={true}
          onReady={handleReady}
          onPlay={handlePlay}
          controls
          allowfullscreen
          width="940px" 
          height="530px"
        />
      </div>
      
    </div>
  );
};

export default VideoPlayer;
