import ReactPlayer from "react-player";
import React, { useState } from "react";

const VideoPlayer = () => {
  const [playing, setPlaying] = useState(false);
  const [muted, setMuted] = useState(true);

  const handleReady = () => {
    setPlaying(true);
    setMuted(false);
  };

  const handlePlay = () => {
    setPlaying(true);
    setMuted(false);
  };

  const videos = [
    {
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
  ];

  return (
    <div className="container">
      <div className="row">
        {videos.map((video, index) => (
          <div
            key={index}
            className="col-lg-3 col-md-6 col-sm-12 d-flex justify-content-center mb-4"
          >
            <div
              className="position-relative overflow-hidden rounded-3"
              style={{ paddingTop: `${(1 / 1.85) * 100}%` }}
            >
              <ReactPlayer
                className="position-absolute top-0 start-0"
                url={video.url}
                playing={playing}
                muted={muted}
                autoPlay={true}
                onReady={handleReady}
                onPlay={handlePlay}
                controls
                allowfullscreen
                width="100%"
                height="100%"
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideoPlayer;
