import React, { useRef, useEffect, useState } from 'react';
import ReactPlayer from 'react-player';

const WatchPage = () => {

  const videos = [
    { 
      id: 1,
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    },
    { 
      id: 2,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 3,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 4,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 5,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 6,
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    },
    { 
      id: 7,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 8,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 9,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 10,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 11,
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    },
    { 
      id: 12,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 13,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 14,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 15,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
   
  ];

  // hàm xáo trộn danh sách video
  const shuffleVideos = (videos) => {
    for (let i = videos.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [videos[i], videos[j]] = [videos[j], videos[i]];
    }
    return videos;
  };

  const shuffledVideos = shuffleVideos(videos); // xáo trộn danh sách video
  const videoRefs = useRef(Array(videos.length).fill().map(() => React.createRef()));
  const [playingVideos, setPlayingVideos] = useState([]); // danh sách các video đang phát

  const handleError = (error) => {
    console.log('An error occurred:', error);
  };

  // hàm kiểm tra xem video có đang nằm trong phạm vi màn hình hay không
  const handleIntersection = (entries) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        const videoId = entry.target.id;
        setPlayingVideos((prevPlayingVideos) => [...prevPlayingVideos, videoId]);
      } else {
        const videoId = entry.target.id;
        setPlayingVideos((prevPlayingVideos) => prevPlayingVideos.filter((id) => id !== videoId));
      }
    });
  };
 
  useEffect(() => {
    // tạo một instance của Intersection Observer API
    const observer = new IntersectionObserver(handleIntersection, {
      threshold: 0.5, // nếu hơn 50% của video nằm trong phạm vi màn hình thì coi là video đang hiển thị trên màn hình
    });
    const refs = videoRefs.current;
  refs.forEach((ref) => {
    if (ref.current) {
      observer.observe(ref.current);
    }
  });

  return () => {
    refs.forEach((ref) => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    });
  };
}, []);

  return (
    <div className="max-w-screen max-h-screen overflow-auto">
      <div className="flex flex-wrap justify-center items-center mt-[60px]">
        {shuffledVideos.map((video, i) => (
          <div key={video.id} className="w-1/2 h-1/2 flex justify-center items-center mb-[45px] shadow-2xl rounded-2xl overflow-hidden">
            <ReactPlayer
              ref={(ref) => videoRefs.current[i] = ref} // lưu ref của video
              data-id={video.id} // lưu id của video
              url={video.url}
              playing={playingVideos.includes(video.id)}
              muted={false}
              autoPlay={true}
              onError={handleError}
              // width="960px"
              // height="540px"
              controls
              allowFullScreen={true}
              config={{
                youtube: {
                  playerVars: { showinfo: 1 }
                }
              }}
             // loading="lazy" // sử dụng lazy load
            /> 
          </div>
        ))}
      </div>
    </div>
  );
};

export default WatchPage;
