import ReactPlayer from 'react-player';
import React, { useState } from 'react';
  

const VideoPlayer = () => {
    const [playing, setPlaying] = useState(false);

  const handleReady = () => {
    setPlaying(true);
  };
  const handlePlay = () => {
    setPlaying(true);
  };
    return (
        <div className="relative w-full h-0" style={{paddingTop: '56.25%'}}>
          <ReactPlayer
            url="https://www.youtube.com/watch?v=zHDECJy0p7k"
            className="absolute top-0 left-0 w-full h-full"
            width="50%"
            height="50%"
            playing={playing}
        muted={true}
        onReady={handleReady}
        onPlay={handlePlay}
          controls
          allowfullscreen
          />
        </div>
      );
};

export default VideoPlayer;
