const WatchPage = () => {
  
  const [videos] = useState([
    { 
      id: 1,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 2,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 3,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 4,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 5,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 6,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 7,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 8,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 9,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 12,
      url: "https://www.youtube.com/watch?v=WSS6lnszkrc",
    },
  ]);
  const { id } = useParams();
  const videoRefs = useRef([]);
  const [playingVideos, setPlayingVideos] = useState([]);
  const [videoIds, setVideoIds] = useState(videos.map(video => video.id));
  const [videoKey, setVideoKey] = useState(0); // Thêm biến state videoKey
  const randomVideo = videos[Math.floor(Math.random() * videos.length)]; // Lấy một video ngẫu nhiên
  useEffect(() => {
    setVideoIds(videos.map(video => video.id));
    setVideoKey(prevKey => prevKey + 1); // Tăng giá trị của videoKey mỗi khi danh sách thay đổi
  }, [videos]);

  useEffect(() => {
    setVideoIds(videos.map(video => video.id));
    setVideoKey(prevKey => prevKey + 1);
    // Cập nhật danh sách video khi id video thay đổi
  }, [id, videos]);
  
  const handleVideoChange = useCallback((videoId) => {
    if (playingVideos.includes(videoId) || !videoIds.includes(videoId)) {
      setPlayingVideos([]);
    } else {
      // Tạm dừng tất cả các video
      const pausePromises = videoRefs.current.map((ref) => {
        if (ref.current && ref.current.dataset.id !== videoId && ref.current.getCurrentTime() > 0 && !ref.current.ended && !ref.current.paused) {
          return ref.current.pause();
        }
        return Promise.resolve();
      });
      Promise.all(pausePromises).then(() => {
        // Trở về điểm bắt đầu của tất cả các video
        videoRefs.current.forEach((ref) => {
          if (ref.current && ref.current.dataset.id !== videoId) {
            ref.current.seekTo(0);
          }
        });
        // Phát video mới
        setPlayingVideos([videoId]);
      });
    }
  }, [playingVideos, videoIds]);

  const handleIntersection = useCallback(
    (entries) => {
      if (Array.isArray(entries)) {
        entries.forEach((entry) => {
          const videoId = entry.target.dataset.id;
          const isPlaying = playingVideos.includes(videoId);
          const videoRef = videoRefs.current.find((ref) => ref.current.dataset.id === videoId);
  
          if (entry.isIntersecting && entry.intersectionRatio >= 0.5 && !isPlaying) {
            videoRef.seekTo(0); // Đưa video về vịtrí ban đầu
            videoRef.getInternalPlayer().playVideo(); // Phát video
            setPlayingVideos((prevPlayingVideos) => [...prevPlayingVideos, videoId]);
          } else if ((!entry.isIntersecting || entry.intersectionRatio < 0.5) && isPlaying) {
            videoRef.getInternalPlayer().pauseVideo(); // Tạm dừng video
            setPlayingVideos((prevPlayingVideos) => prevPlayingVideos.filter((id) => id !== videoId));
          }
        });
      }
    },
    [playingVideos]
  );

  return (
    <div className="max-w-screen max-h-screen overflow-auto ">
      <div className="flex flex-wrap justify-center items-center mt-[105px] mb-[45px]">
        <div
          key={randomVideo.id}
          className="flex justify-center items-center mb-[45px] shadow-2xl rounded-2xl overflow-hidden bg-white"
        >
          <ReactPlayer
            key={`${randomVideo.id}-${videoKey}`} // Sử dụng key prop với giá trị duy nhất để tránh việc nhảy lên đầu trang
            ref={(ref) => (videoRefs.current[randomVideo.id - 1] = ref)}
            data-id={randomVideo.id}
            url={randomVideo.url}
            playing={playingVideos.includes(randomVideo.id)}
            onPlay={() => handleVideoChange(randomVideo.id)}
            autoPlay={true}
            width="960px"
            height="540px"
            controls
            allowFullScreen={true}
            config={{
              youtube: {
                playerVars: { showinfo: 1, playsinline: 1 },
                preload: "auto",
                buffered: "auto",
              },
            }}
            loading="lazy"
            preload="true"
            buffered= "auto"
          />
          <InView
            as="div"
            rootMargin="-50% 0px"
            onChange={handleIntersection}
            data-id={`helo${randomVideo.id}`}
          />
        </div>
      </div>
    </div>
  );
};
