import React from 'react';

const MenuModal = ({ isOpen, onClose }) => {
  const handleBackdropClick = (event) => {
    if (event.target === event.currentTarget) {
      onClose();
    }
  };

  return (
    <>
      {isOpen && (
        <div className="fixed top-0 left-0 w-full h-full flex justify-center items-center bg-black bg-opacity-50" onClick={handleBackdropClick}>
          <div className="bg-white p-4 rounded shadow">
            {/* Nội dung menu */}
            <ul className="list-none p-0">
              <li className="py-2">Option 1</li>
              <li className="py-2">Option 2</li>
              <li className="py-2">Option 3</li>
            </ul>
            <button className="mt-4 px-4 py-2 bg-blue-500 text-white rounded" onClick={onClose}>Đóng</button>
          </div>
        </div>
      )}
    </>
  );
};

export default MenuModal;
