import ReactPlayer from 'react-player';
import React, { useState } from 'react';

const VideoPlayer = () => {
  const [playing, setPlaying] = useState(false);
  const [muted, setMuted] = useState(true);

  const handleReady = () => {
    setPlaying(true);
    setMuted(false);
  };

  const handlePlay = () => {
    setPlaying(true);
    setMuted(false);
  };

  const videos = [
    {
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    }
  ];

  return (
    <div className="max-w-screen max-h-screen overflow-auto">
      <div className="flex flex-wrap justify-center">
        {videos.map((video, index) => (
          <div key={index} className="flex justify-center mt-12 w-[940px] min-h-[530px]">
            <ReactPlayer
              url={video.url}
              playing={playing}
              muted={muted}
              autoPlay={true}
              onReady={handleReady}
              onPlay={handlePlay}
              controls
              allowfullscreen
              
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideoPlayer;
