import React, { useRef, useEffect, useState, useCallback } from 'react';
import ReactPlayer from 'react-player/lazy';
import { InView } from 'react-intersection-observer';

const WatchPage = () => {

  const videoRefs = useRef([]);
  const videos = [
    { 
      id: 1,
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
    },
    { 
      id: 2,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
    { 
      id: 12,
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
    },
  ];
  const [playingVideos, setPlayingVideos] = useState([]);
  const handleVideoChange = useCallback(
    (videoId) => {
      if (playingVideos.includes(videoId)) {
        setPlayingVideos((prevPlayingVideos) =>
          prevPlayingVideos.filter((id) => id !== videoId)
        );
      } else {
        setPlayingVideos([videoId]);
      }
    },
    [playingVideos]
  );

  const handleIntersection = useCallback(
    (entries) => {
      entries.forEach((entry) => {
        const videoId = entry.target.dataset.id;
        const isPlaying = playingVideos.includes(videoId);
        const videoRef = videoRefs.current.find(
          (ref) => ref.current.dataset.id === videoId
        );

        if (entry.isIntersecting && !isPlaying) {
          videoRef.seekTo(0);
          videoRef.play();
          setPlayingVideos((prevPlayingVideos) => [
            ...prevPlayingVideos,
            videoId,
          ]);
        } else if (!entry.isIntersecting && isPlaying) {
          videoRef.pause();
          setPlayingVideos((prevPlayingVideos) =>
            prevPlayingVideos.filter((id) => id !== videoId)
          );
        }
      });
    },
    [playingVideos]
  );
  const [playTime, setPlayTime] = useState(0);

const handleProgress = (state) => {
    setPlayTime(state.playedSeconds);
}
const formatTime = (time) => {
  const date = new Date(time * 1000);
  const hour = date.getUTCHours();
  const minute = date.getUTCMinutes();
  const second = ('0' + date.getUTCSeconds()).slice(-2);
  if (hour) {
    return `${hour}:${('0' + minute).slice(-2)}:${second}`;
  }
  return `${minute}:${second}`;
}
  return (
    <div className="max-w-screen max-h-screen overflow-auto">
      <div className="flex flex-wrap justify-center items-center mt-[60px]">
        {videos.map((video) => (
          <div
            key={video.id}
            className="flex justify-center items-center mb-[45px] shadow-2xl rounded-2xl overflow-hidden"
            //onPlay={() => handleVideoChange(video.id)}
          >
            <ReactPlayer
              ref={(ref) => (videoRefs.current[video.id] = ref)}
              data-id={video.id}
              url={video.url}
              playing={playingVideos.includes(video.id)}
              muted={true}
              autoPlay={false}
              width="960px"
              height="540px"
              controls
              onProgress={handleProgress}
              allowFullScreen={true}
              config={{
                youtube: {
                  playerVars: { showinfo: 1 },
                },
              }}
              loading="lazy"
            />
          </div>
        ))}
      </div>
      {videoRefs.current.length > 0 && (
        <InView
          rootMargin="0px"
          threshold={0.5}
          onChange={handleIntersection}
        >
          {videos.map((video) => (
            <div
              key={video.id}
              data-id={video.id}
              ref={(ref) => (videoRefs.current[video.id] = ref)}
            />
          ))}
        </InView>
      )}
    </div>
  );
};

export default WatchPage;
