import React, { useState } from 'react';
import YouTube from 'react-youtube';

const WatchPage = () => {
  const [playing, setPlaying] = useState(false);
  const [muted, setMuted] = useState(true);

  const handleReady = () => {
    setPlaying(true);
    setMuted(false);
  };

  const handlePlay = () => {
    setPlaying(true);
    setMuted(false);
  };

  const videos = [
    // { 
