import ReactPlayer from 'react-player';
import React, { useState } from 'react';

const WatchPage = () => {
  const [playing, setPlaying] = useState(false);
  const [muted, setMuted] = useState(true);

  const handleReady = () => {
    setPlaying(true);
    setMuted(false);
  };

  const handlePlay = () => {
    setPlaying(true);
    setMuted(false);
  };

  const videos = [
    {
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    }
  ];

  return (
    <div className="max-w-screen max-h-screen overflow-auto">
      <div className="flex flex-wrap justify-center mb-[45] ">
        {videos.map((video, index) => (
          <div key={index} className="flex justify-center mt-[45px] shadow-2xl rounded-2xl overflow-hidden ">
            <ReactPlayer
              url={video.url}
              playing={playing}
              muted={muted}
              autoPlay={true}
              onReady={handleReady}
              onPlay={handlePlay}
              width= "940px"
              height= "530px"
              controls
              allowfullscree
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default WatchPage;
