import ReactPlayer from 'react-player';
import React, { useState } from 'react';

const VideoPlayer = () => {
  const [playing, setPlaying] = useState(false);
  const [muted, setMuted] = useState(true);

  const handleReady = () => {
    setPlaying(true);
    setMuted(false);
  };

  const handlePlay = () => {
    setPlaying(true);
    setMuted(false);
  };

  const videos = [
    {
      url: "https://www.youtube.com/watch?v=KxaX-eYPjQw",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    },
    {
      url: "https://www.youtube.com/watch?v=d1_JKFy_fIk",
      width: "940px",
      height: "530px",
    }
  ];

  return (
    <div className=" flex flex-wrap justify-center">

      {videos.map((video, index) => (
        <div key={index} className="flex justify-center mt-8">
          <ReactPlayer
            url={video.url}
            playing={playing}
            muted={muted}
            autoPlay={true}
            onReady={handleReady}
            onPlay={handlePlay}
            controls
            allowfullscreen
            width={video.width}
            height={video.height}
          />
        </div>
      ))}
    </div>
  );
};

export default VideoPlayer;
